import React, {useEffect, useState} from 'react';
import {Div, Input, Button, Form} from './search';
import {SearchOutlined} from '@ant-design/icons';
import axios from "axios";

const Search = () => {
    const [tagValue, setTagValue] = useState({
        hashtag: ''
    });
    const callTagData = async () => {
        await axios.get('/page/hashtag')
            .then((res) => {
                console.log(res)
            })
    }

    useEffect(() => {
        callTagData()
    }, [])

    const onChange = (e) => {
        const {name, value} = e.target;
        setTagValue({
            [name]: value,
        });
    }

    const onSubmit = async (e) => {
        e.preventDefault();
        await axios.post('/page/hashtag', tagValue)
    }


    return (
        <Div>
            <Form onSubmit={onSubmit} id="hashtag-form">
                <Input onChange={onChange} value={tagValue} name="hashtag" type="text" name="hashtag" placeholder='검색' />
                <Button>취소</Button>
            </Form>

        </Div>
    );
};

export default Search;
