import React, {useCallback, useEffect, useState} from 'react';
import { Div, Img, A, Form, Input, Button } from './signup';
import axios from "axios";

const SignUp = () => {

    const [userInfo, setUserInfo] = useState({
        email: '',
        password: '',
        passwordCheck: '',
        nick: ''
    });
    const { email, password, passwordCheck, nick } = userInfo;

    const onUserInfoChange = useCallback((e) => {
        const { name, value } = e.target;
        setUserInfo({
            ...userInfo,
            [name]: value,
        });
    });
    //
    const callApi = () => {
        fetch("http://localhost:4848/join", {
        method: "post", //통신방법
            headers: {
            "content-type": "application/json",
        },
        body: JSON.stringify(userInfo), //textbox라는 객체를 보냄
    }).then((res) => {
        console.log(res.json())
        });
    }
    const onSubmit = (e) => {
        e.preventDefault();
        callApi();
    }

    return (
        <Div container>
            <Div info>
                <Div img>
                    <Img src={process.env.PUBLIC_URL + '/Img/main-logo.png'} />
                </Div>
                <Div common phrases>
                    친구들의 사진과 동영상을 보려면 가입하세요.
                </Div>
                <Div common flex kakao>
                    <A id="kakao" href="/auth/kakao" className="btn">KaKao로 로그인</A>
                </Div>
                <Div lineContain flex>
                    <Div line></Div>
                    또는
                    <Div line></Div>
                </Div>
                {/* action="/auth/join" method="post"*/}
                <Form onSubmit={onSubmit}>
                    <Div flex>
                        <Input
                            type="email"
                            name="email"
                            onChange={onUserInfoChange}
                            value={email}
                            placeholder="이메일 주소"
                            required
                        />
                    </Div>
                    <Div flex>
                        <Input
                            type="text"
                            name="nick"
                            onChange={onUserInfoChange}
                            value={nick}
                            placeholder="사용자 이름"
                            required
                        />
                    </Div>
                    <Div flex>
                        <Input
                            type="password"
                            name="password"
                            onChange={onUserInfoChange}
                            value={password}
                            placeholder="비밀번호"
                            required
                        />
                    </Div>
                    <Div flex>
                        <Input
                            type="password"
                            name="passwordCheck"
                            onChange={onUserInfoChange}
                            value={passwordCheck}
                            placeholder="비밀번호 확인"
                            required
                        />
                    </Div>
                    <Div flex>
                        <Button type="submit">
                            가입
                        </Button>
                    </Div>
                </Form>
            </Div>

            <Div info signin flex>
                <p>계정이 없으신가요?</p>
                <A signin id="join" href="/signin" className="btn">로그인</A>
            </Div>
        </Div>
    );
};

export default SignUp;
