            .then((res) => {
                console.log('success')
            })
    }

    return (
        <Div container>
            <Header>
                <H2>{User.nick}</H2>
                {
                    User.id === user.id || valueMatchState
                    ? null
                    : <button onClick={onFollow}>팔로우 하기</button>
                }
            </Header>
            <StyledCard
                hoverable
                cover={<Img alt="example" src={img} />}
            >
                <Div flex>
                    {
                        goodState
                        ? <HeartTwoTone onClick={onClick} twoToneColor="#eb2f96" />
                        : <HeartOutlined onClick={onClick} />
                    }
                    <SaveOutlined />
                </Div>
                <Div content>
                    <H2>{content}</H2>
                </Div>
            </StyledCard>
        </Div>
    );
};

export default Posts;
