import styled, {css} from "styled-components";

export const Div = styled.div`

    ${props => props.container && css`
      height: 600px;
      max-height: 600px;
      overflow: scroll;
      border: 1px solid black;
      display: flex;
      justify-content: center;
      position: relative;
    `}

    ${props => props.flex && css`
      display: flex;
      flex-direction: column;
      margin-bottom: 10px;
    `}
    `;
