import React, {useState} from 'react';
import {Button, Div, Img, P, Span, Ul, Li} from './profile';
import {AppstoreOutlined, PlusOutlined, UserOutlined} from "@ant-design/icons";

const tabObj = {
    0: 'jake',
    1: 'pin'
}

const tabStyle1 = {
    0: '#fff',
    1: 'darkgrey'
}
const tabStyle2 = {
    0: 'darkgrey',
    1: '#fff'
}


const Profile = ({user}) => {
    const [activeId, setActiveId] = useState(0);

    const clickHandler = (id) => {
        setActiveId(id)
    }
    return (
        <Div container>
            <Div header>
                <Div img>
                    <Img src={process.env.PUBLIC_URL + '/Img/user-icon.jpeg'}/>
                    <P>{user.nick}</P>
                    <Span><PlusOutlined /></Span>
                </Div>

                <Div line>
                    <p>0</p>
                    <p>게시물</p>
                </Div>
                <Div line>
                    <p>
                        {
                            user.Followings && Object.keys(user.Followings).length
                        }
                    </p>
                    <p>팔로잉</p>
                </Div>
                <Div line>
                    <p>
                        {
                            user.Followers && Object.keys(user.Followers).length
                        }
                    </p>
                    <p>
                        팔로워
                    </p>
                </Div>
            </Div>
            <Div line>
                <Button>프로필 편집</Button>
            </Div>
            <Div line>
                <Ul className="tabs">
                    <Li style={{color: tabStyle1[activeId], borderBottom: `1px solid ${tabStyle1[activeId]}` }} onClick={() => clickHandler(0)}>
                        <AppstoreOutlined />
                    </Li>
                    <Li style={{color: tabStyle2[activeId], borderBottom: `1px solid ${tabStyle2[activeId]}` }} onClick={() => clickHandler(1)}>
                        <UserOutlined />
                    </Li>
                </Ul>
                <div className="contents">{tabObj[activeId]}</div>
            </Div>
        </Div>
    );
};

export default Profile;
