import React, {useRef, useState} from 'react';
import {Input, Div, Form, Label, Textarea, Button, Img} from './write';
import axios from 'axios';

const Write = () => {
    const [imgBase64, setImgBase64] = useState(""); // 파일 base64
    const [imgFile, setImgFile] = useState(null);	//파일
    const img = useRef();

    // //
    // const callApi = () => {
    //     fetch("http://localhost:4848/auth/join", {
    //         method: 'POST', // *GET, POST, PUT, DELETE, etc.
    //         mode: 'cors', // no-cors, cors, *same-origin
    //         cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
    //         credentials: 'same-origin', // include, *same-origin, omit
    //         headers: {
    //             'Content-Type': 'application/json',
    //             // 'Content-Type': 'application/x-www-form-urlencoded',
    //         },
    //         redirect: 'follow', // manual, *follow, error
    //         referrer: 'no-referrer', // no-referrer, *client
    //         body: JSON.stringify(userInfo),
    // }).then((res) => {
    //     console.log(res.data)
    //     });
    // }
    // const onSubmit = (e) => {
    //     e.preventDefault();
    //     callApi();
    // }

    const handleChangeFile = (event) => {
        let reader = new FileReader();

        reader.onloadend = () => {
            // 2. 읽기가 완료되면 아래코드가 실행됩니다.
            const base64 = reader.result;
            if (base64) {
                setImgBase64(base64.toString()); // 파일 base64 상태 업데이트
            }
        }
        if (event.target.files[0]) {
            reader.readAsDataURL(event.target.files[0]); // 1. 파일을 읽어 버퍼에 저장합니다.
            setImgFile(event.target.files[0]); // 파일 상태 업데이트
        }

        return img.current.style = 'display: block'
    }

    const callApi = (event) => {
        const formData = new FormData();
        console.log(formData)
        console.log(event.target.files[0])
        Object.assign(formData, {img : event.target.files[0]})
        console.log(formData)
        // axios.post('http://localhost:4848/post/img', formData)
        //     .then((res) => {
        //         console.log(res)
        //     })
        //     .catch((err) => {
        //         console.error(err);
        //     });
        fetch("http://localhost:4848/post/img", {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, cors, *same-
            headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: formData,
        }).then((res) => {
            console.log(res.data)
        });
    }

    const onSubmit = (e) => {
        e.preventDefault();
        callApi()
    }

    return (
        <Div container>
            <Form id="twit-form" onSubmit={onSubmit}  encType="multipart/form-data">
                <Div img>
                    <Label className="input-file-button" for="imgFile">
                        이미지 업로드
                    </Label>
                    <Input img type="file" name="imgFile" id="imgFile" required onChange={handleChangeFile} />
                </Div>
                <Div showImg>
                    <Img ref={img} src={imgBase64} />
                </Div>
                <Div>
                    <Textarea name="content" maxLength="140" placeholder="문구 입력..." />
                </Div>
                <Button type="submit">업로드</Button>
            </Form>
        </Div>
    );
};

export default Write;
