import styled, {css} from "styled-components";

export const Div = styled.div`
  display:flex;

  ${props => props.line && css`
    align-items: center;
    flex-direction: column;
  `}

  ${props => props.img && css`
    width: 30px;
    height: 30px;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
  `}
`;
