import Styled, {css} from 'styled-components';
import styled from "styled-components";

export const Div = styled.div`
  box-sizing: border-box;
  width: 268px;
  height: 44px;

  ${props => props.flex && css`
    display: flex;
    align-items: center;
    justify-content: center;
  `}

  ${props => props.container && css`
    width: 100vw;
    height: 100vh;
    background: #fafafa;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
  `}

  ${props => props.info && css`
    width: 350px;
    height: 382px;
    background: #ffffff;
    border: 1px solid #b8b8b8;
    margin-bottom: 10px;
  `}

  ${props => props.img && css`
    width: 200px;
    height: auto;
    margin: 10px auto;
  `}

  ${props => props.phrases && css`
    margin: auto;
    font-size: 18px;
    text-align: center;
  `}

  ${props => props.kakao && css`
    margin: auto;
    background: #d7d703;
  `}
`;

export const Img = styled.img`
  width: 100%;
  height: auto;

  ${props => props.kakao && css`
    width: 20px;
    height: auto;
    margin-right: 5px;
  `}
`;

export const A = styled.a`
  text-decoration: none;
  color: #fff;
  font-weight: bold;
  cursor: pointer;

  ${props => props.signup && css`
    margin-left: 3px;
    color: #ffffff;
  `}
`;
