import styled, {css} from "styled-components";

export const Form = styled.form`
  width: 100vw;
`;

export const Div = styled.div`
  ${props => props.img && css`
    display: flex;
    flex-direction: column;
  `}  
`;

export const Input = styled.input`

    ${props => props.img && css`
      width: 100%;
      background: #000;
      color: #000;
      font-size: 35px;
    `}

`;
