import React, {useEffect, useState} from 'react';
import {Div, Header, Img, StyledCard, H2} from './posts';
import {HeartOutlined, HeartTwoTone, SaveOutlined, SmallDashOutlined} from "@ant-design/icons";
import axios from "axios";

const Posts = ({postsData}) => {
    const { content, User, img } = postsData;
    return (
        <Div container>
            <Header>
                <H2>{User.nick}</H2>
                <SmallDashOutlined style={{fontSize: '24px'}} />
            </Header>
            <StyledCard
                hoverable
                cover={<Img alt="example" src={img} />}
            >
                <Div flex>
                    <HeartOutlined />
                    <HeartTwoTone twoToneColor="#dd00ff" />
                    <SaveOutlined />
                </Div>
                <Div content>
                    <H2>{content}</H2>
                </Div>
            </StyledCard>
        </Div>
    );
};

export default Posts;
