import React, {useCallback, useState} from 'react';
import { Div, Img, A, Form, Input, Button } from './signup';

const SignUp = () => {
    const [userInfo, setUserInfo] = useState({
        email: '',
        password: '',
        passwordCheck: '',
        nickname: ''
    });
    const { email, password, passwordCheck, nickname } = userInfo;

    const onUserInfoChange = useCallback((e) => {
        const { name, value } = e.target;
        setUserInfo({
            ...userInfo,
            [name]: value,
        });
    });

    // 비밀번호 유효성 검사
    const confirmPassword = useCallback(() => {
        const regExp = /(?=.*\d{1,50})(?=.*[~`!@#$%\\^&*()-+=]{1,50})(?=.*[a-z]{1,50}).{8,12}$/;
        const isValidPassword = regExp.test(password);
        if (password === '') {
            return alert('비밀번호를 입력해 주세요');
        }
        if (!isValidPassword) {
            return alert('비밀번호를 영문자, 숫자, 특수문자를 포함한 8글자 이상으로 구성해 주세요.');
        }
        return Promise.resolve();
    }, []);

    const onSubmit = (e) => {
        e.preventDefault();
        confirmPassword();
        console.log(userInfo);
    }

    return (
        <Div container>
            <Div info>
                <Div img>
                    <Img src={process.env.PUBLIC_URL + '/Img/main-logo.png'} />
                </Div>
                <Div common phrases>
                    친구들의 사진과 동영상을 보려면 가입하세요.
                </Div>
                <Div common flex kakao>
                    <A id="kakao" href="/auth/kakao" className="btn">KaKao로 로그인</A>
                </Div>
                <Div lineContain flex>
                    <Div line></Div>
                    또는
                    <Div line></Div>
                </Div>
                {/* action="/auth/join" method="post"*/}
                <Form
                    onSubmit={onSubmit}
                >
                    <Div flex className="input-group">
                        <Input
                            type="email"
                            name="email"
                            onChange={onUserInfoChange}
                            value={email}
                            placeholder="이메일 주소"
                        />
                    </Div>
                    <Div flex className="input-group">
                        <Input
                            type="text"
                            name="nickname"
                            onChange={onUserInfoChange}
                            value={nickname}
                            placeholder="사용자 이름"
                        />
                    </Div>
                    <Div flex className="input-group">
                        <Input
                            type="password"
                            name="password"
                            onChange={onUserInfoChange}
                            value={password}
                            placeholder="비밀번호"
                        />
                    </Div>
                    <Div flex className="input-group">
                        <Input
                            type="password"
                            name="passwordCheck"
                            onChange={onUserInfoChange}
                            value={passwordCheck}
                            placeholder="비밀번호 확인"
                        />
                    </Div>
                    <Div flex>
                        <Button
                            type="submit"
                        >
                            가입
                        </Button>
                    </Div>
                </Form>
            </Div>

            <Div info signin flex>
                <p>계정이 없으신가요?</p>
                <A signin id="join" href="/signin" className="btn">로그인</A>
            </Div>
        </Div>
    );
};

export default SignUp;
