import React, {useCallback, useState, useEffect} from 'react';
import { Div, Img, Button, Input, Form, A } from './signin';
import axios from "axios";

const SignIn = () => {
    const [userInfo, setUserInfo] = useState({
        email: '',
        password: '',
    });

    const onUserInfoChange = useCallback((e) => {
        const { name, value } = e.target;
        setUserInfo({
            ...userInfo,
            [name]: value,
        });
    });

    const callApi = () => {
        fetch("http://localhost:4848/auth/login", {
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, cors, *same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            credentials: 'same-origin', // include, *same-origin, omit
            headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: 'follow', // manual, *follow, error
            referrer: 'no-referrer', // no-referrer, *client
            body: JSON.stringify(userInfo),
        }).then((res) => {
            console.log(res.data)
        });
    }
    const onSubmit = (e) => {
        e.preventDefault();
        callApi();
    }

    const { email, password } = userInfo;
    return (
        <Div container>
            <Div info>
                <Div className="logo" img>
                    <Img src={process.env.PUBLIC_URL + '/Img/main-logo.png'}/>
                </Div>
                <div className="user-info">
                    <Form onSubmit={onSubmit}>
                        <Div flex>
                            <Input
                                id="email"
                                type="email"
                                name="email"
                                value={email}
                                onChange={onUserInfoChange}
                                placeholder="전화번호, 사용자 이름 또는 이메일"
                                required
                                autoFocus
                            />
                        </Div>
                        <Div flex className="input-group">
                            <Input
                                id="password"
                                type="password"
                                name="password"
                                value={password}
                                onChange={onUserInfoChange}
                                placeholder="비밀번호"
                                required
                            />
                        </Div>
                        <Div flex>
                            <Button id="login" type="submit" className="btn">로그인</Button>
                        </Div>
                    </Form>
                    <Div lineContain flex>
                        <Div line></Div>
                        또는
                        <Div line></Div>
                    </Div>
                    <Div flex>
                        <Img kakao src={process.env.PUBLIC_URL + '/Img/Kakao_logo.jpeg'} />
                        <A id="kakao" href="/auth/kakao" className="btn">KaKao로 로그인</A>
                    </Div>
                </div>
            </Div>

            <Div info signup flex>
                <p>계정이 없으신가요?</p>
                <A signup id="join" href="/signup" className="btn">가입하기</A>
            </Div>
        </Div>
    );
};

export default SignIn;
