import React, {useState} from 'react';
import {Input, Div, Form, Label, Textarea, Button} from './write';
import axios from 'axios';

const Write = () => {
    const [imgBase64, setImgBase64] = useState(""); // 파일 base64
    const [imgFile, setImgFile] = useState(null);	//파일

    const handleChangeFile = (event) => {
        let reader = new FileReader();

        reader.onloadend = () => {
            // 2. 읽기가 완료되면 아래코드가 실행됩니다.
            const base64 = reader.result;
            if (base64) {
                setImgBase64(base64.toString()); // 파일 base64 상태 업데이트
            }
        }
        if (event.target.files[0]) {
            reader.readAsDataURL(event.target.files[0]); // 1. 파일을 읽어 버퍼에 저장합니다.
            setImgFile(event.target.files[0]); // 파일 상태 업데이트
        }
    }

    return (
        <Div container>
            <Form id="twit-form" action="/post" method="post" encType="multipart/form-data">
                <Div img>
                    <Label className="input-file-button" for="img">
                        이미지 업로드
                    </Label>
                    <Input img type="file" id="img" required onChange={handleChangeFile} />
                </Div>
                <div className="img-preview">
                    <img id="img-preview" src="" />
                </div>
                <Div>
                    <Textarea id="twit" name="content" maxLength="140"></Textarea>
                </Div>
                <Button id="twit-btn" type="submit" className="btn">업로드</Button>
            </Form>
        </Div>
    );
};

export default Write;
