import React, {useState} from 'react';
import {Input, Div, Form, Label, Textarea, Button} from './write';
import axios from 'axios';

const Write = () => {
    const [file, setFile] = useState('');

    const onLoaded = (e) => {
        if (document.getElementById('img')) {
            document.getElementById('img').addEventListener('change', function(e) {
                const formData = new FormData();
                console.log(this, this.files);
                formData.append('img', this.files[0]);
                axios.post('/post/img', formData)
                    .then((res) => {
                        document.getElementById('img-url').value = res.data.url;
                        document.getElementById('img-preview').src = res.data.url;
                        document.getElementById('img-preview').style.display = 'inline';
                    })
                    .catch((err) => {
                        console.error(err);
                    });
            });
        }
    }

    return (
        <Div container>
            <Form id="twit-form" action="/post" method="post" encType="multipart/form-data">
                <Div img>
                    <Label className="input-file-button" for="img">
                        이미지 업로드
                    </Label>
                    <Input img type="file" id="img" required onChange={onLoaded} />
                </Div>
                <div className="img-preview">
                    <img id="img-preview" src="" />
                </div>
                <Div>
                    <Textarea id="twit" name="content" maxLength="140"></Textarea>
                </Div>
                <Button id="twit-btn" type="submit" className="btn">업로드</Button>
            </Form>
        </Div>
    );
};

export default Write;
