import styled, {css} from "styled-components";

export const Div = styled.div`
  box-sizing: border-box;
  width: 268px;
  height: 38px;

  ${props => props.container && css`
    width: 100vw;
    height: 100vh;
    background: #eee;
    display: flex;
    align-items: center;
    justify-content: center;
  `}

  ${props => props.info && css`
    width: 350px;
    height: 382px;
    background: #ffffff;
  `}

  ${props => props.img && css`
    width: 200px;
    height: auto;
    margin: 20px auto;
  `}
`;

export const Img = styled.img`
  width: 100%;
  height: auto;
`;

export const Button = styled.button`
  width: 268px;
  height: 30px;
  font-weight: bold;
  color: white;
  background: lightskyblue;
  border: none;
  border-radius: 5px;
`;

export const Input = styled.input`
  box-sizing: border-box;
  width: 268px;
  height: 30px;
  background: #eee;
  padding: 10px;
  border: 1px solid #b8b8b8;
`;
