import styled, {css} from "styled-components";

export const Div = styled.div`
  ${props => props.container && css`
    width: 100vw;
    height: 100vh;
    background: #eee;
    display: flex;
    align-items: center;
    justify-content: center;
  `}

  ${props => props.info && css`
    width: 500px;
    height: 700px;
    background: #ffffff;
  `}

  ${props => props.img && css`
    width: 200px;
    height: auto;
    margin: 50px auto;
  `}
`;

export const Img = styled.img`
  width: 100%;
`;
