import styled, {css} from "styled-components";

export const Div = styled.div`
  display:flex;
  color: darkgray;

  ${props => props.container && css`
    width: 100vw;
    height: calc(100vh - 102px);
    background: #000;
  `}

  ${props => props.header && css`
    width: 100vw;
    height: 120px;
    padding: 10px 0;
    justify-content: space-around;
    align-items: center;
  `}

  ${props => props.line && css`
    align-items: center;
    flex-direction: column;
  `}

  ${props => props.img && css`
    width: 80px;
    height: 80px;
    align-items: center;
    justify-content: center;
    border-radius: 50px;
    position: relative;
    overflow: hidden;
  `}
`;

export const Img = styled.img`
  width: 100%;
  height: auto;
`;

export const P = styled.p`
  position: absolute;
  bottom: -20px;
  left: 0;
  color: darkgray;
`;
