import React, {useEffect, useState} from 'react';
import {Img} from "./profilePost";

const ProfilePost = ({postData, user}) => {
    const [postImg, setPostImg] = useState('');
    useEffect(() => {
        postData.map((post, i) => {
            post.id === user.id && setPostImg(post.img)
        })
    })

    return (
        <div>
            <Img src={postImg} />
        </div>
    );
};

export default ProfilePost;
